import React, { useState } from "react";
import { Typography, Box, Button, Dialog} from "@mui/material";
import AddIcon from '@mui/icons-material/Add'; 
import { JobForm
} from "../components/jobForm";


function HomePage () {
    const [openDailog, setOpenDialog] = useState(false)

    const handleOpenDialog = () => {
        setOpenDialog(true);
      };

    const handleCloseDialog = () => {
        setOpenDialog(false);
    };



    return (
        <Box sx={{ 
            position: 'relative',
            height: '100vh', // Full viewport height
        }}>

            {/* Top center Job Tracker text */}
            <Typography variant="h3" sx={{ 
                textAlign: 'center',
                position: 'absolute',
                top: 16, // Adjust from the top
                left: '50%',  // Position in the middle
                transform: 'translateX(-50%)', // Adjust for text width
            }}>
                Job Tracker
            </Typography>

            {/* Top right Add icon button */}
            <Button 
                onClick={handleOpenDialog}
                variant="outlined" 
                startIcon={<AddIcon />} 
                color="primary" 
                sx={{ 
                    position: 'absolute', 
                    top: 16,
                    right: 16 ,
                }}
            >
            </Button> 

            <Dialog open={openDailog} onClose={handleCloseDialog}>
                <JobForm onClose={handleCloseDialog} />
            </Dialog>
        </Box>
    );
}

export default HomePage;
