import React, { useState , useEffect} from "react";
import { Typography, Box, Button, Dialog, Card, CardContent, CardActions} from "@mui/material";
import AddIcon from '@mui/icons-material/Add'; 
import { JobForm
} from "../components/jobForm";



function HomePage () {
    const [openDailog, setOpenDialog] = useState(false)
    const username = localStorage.getItem('username')
    const password = localStorage.getItem('password')
    const credentials =  btoa(`${username}:${password}`)

    const [jobs, setJobs] = useState([])

    const handleOpenDialog = () => {
        setOpenDialog(true);
      };

    const handleCloseDialog = () => {
        setOpenDialog(false);
    };


    useEffect(() => {
        const fetchJobs = async () => {
            try {
                const response = await fetch(`http://127.0.0.1:8000/api/${username}/jobs`, {
                    method: 'GET',
                    headers: {
                        'Authorization': `Basic ${credentials}`
                    }
                });

                if (response.ok) {
                    const jobsData = await response.json();
                    setJobs(jobsData);
                } else {
                    console.error('Failed to fetch jobs');
                }
            } catch (error) {
                console.error('Error fetching jobs:', error);
            }
        };

        fetchJobs();
    }, [username, credentials]);


    return (
        <Box sx={{ 
            position: 'relative',
            height: '100vh', // Full viewport height
        }}>
            {/* Outer Box for main content (flexDirection: column) */}
            <Box sx={{ 
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                paddingTop: '100px', // Adjust padding as needed
            }}>
                {/* Inner Box for heading and button (flexDirection: row) */}
                <Box sx={{ 
                    display: 'flex',
                    flexDirection: 'row',
                    justifyContent: 'space-between', // Distribute evenly
                    width: '100%', // Take full available width
                    alignItems: 'center', // Vertically center content
                    marginBottom: '20px', // Add margin below
                }}>
                    <Typography variant="h3" sx={{ textAlign: 'center' }}>
                        Job Tracker
                    </Typography>

                    {/* Add Job Button */}
                    <Button 
                        onClick={handleOpenDialog}
                        variant="outlined" 
                        startIcon={<AddIcon />} 
                        color="primary" 
                    >
                        Add Job
                    </Button> 
                </Box>

                {/* Card container */}
                <Box sx={{ 
                    display: 'flex', 
                    flexWrap: 'wrap', 
                    gap: '16px',
                }}>
                    {jobs.map((job) => (
                        <Card key={job.id} sx={{ width: '300px' }}>
                            <CardContent>
                                {/* ... your CardContent ... */}
                            </CardContent>
                            <CardActions>
                                {/* ... your CardActions ... */}
                            </CardActions>
                        </Card>
                    ))}
                </Box>
            </Box>

            {/* Dialog */}
            <Dialog open={openDailog} onClose={handleCloseDialog}>
                <JobForm onClose={handleCloseDialog} />
            </Dialog>
        </Box>
    );
}

export default HomePage;
